SELECT DISTINCT Companies.Name AS Company_Name, ICSRs.ID AS ICSR_ID, CASE WHEN ICSRs.Patient_Initials IS NULL THEN '' ELSE ICSRs.Patient_Initials END AS Patient_Initials, CASE WHEN YearsOfBirth.Name IS NULL THEN '' ELSE YearsOfBirth.Name END AS Patient_YearOfBirth, CASE WHEN Genders.Name IS NULL THEN '' ELSE Genders.Name END AS Patient_Gender, CASE WHEN IsSerious.Name IS NULL THEN '' ELSE IsSerious.Name END AS IsSerious, CASE WHEN SeriousnessCriteria.Name IS NULL THEN '' ELSE SeriousnessCriteria.Name END AS SeriousnessCriterion, AEs.MedDRATerm AS AE_MedDRATerm, CASE WHEN AEs.Start IS NULL THEN 0 ELSE AEs.Start END AS AE_Start, CASE WHEN AEs.Stop IS NULL THEN 0 ELSE AEs.Stop END AS AE_Stop, CASE WHEN Outcomes.Name IS NULL THEN '' ELSE Outcomes.Name END AS Outcome, CASE WHEN DechallengeResults.Name IS NULL THEN '' ELSE DechallengeResults.Name END AS DechallengeResult, CASE WHEN RechallengeResults.Name IS NULL THEN '' ELSE RechallengeResults.Name END AS RechallengeResult, Medications.Name AS SuspectedDrug_Name, CASE WHEN MedicationsPerICSR.TotalDailyDose IS NULL THEN 0 ELSE MedicationsPerICSR.TotalDailyDose END AS TotalDailyDose, CASE WHEN DoseTypes.Name IS NULL THEN '' ELSE DoseTypes.Name END AS DoseType, CASE WHEN MedicationsPerICSR.Allocations IS NULL THEN 0 ELSE MedicationsPerICSR.Allocations END AS Allocations, CASE WHEN MedicationsPerICSR.Start IS NULL THEN 0 ELSE MedicationsPerICSR.Start END AS Treatment_Start, CASE WHEN MedicationsPerICSR.Stop IS NULL THEN 0 ELSE MedicationsPerICSR.Stop END AS Treatment_Stop, CASE WHEN DrugActions.Name IS NULL THEN '' ELSE DrugActions.Name END AS DrugAction FROM Relations JOIN AEs ON AEs.ID = Relations.AE_ID JOIN ICSRs ON ICSRs.ID = AEs.ICSR_ID LEFT JOIN YearsOfBirth ON YearsOfBirth.ID = ICSRs.Patient_YearOfBirth_ID LEFT JOIN Genders ON Genders.ID = ICSRs.Patient_Gender_ID LEFT JOIN IsSerious ON IsSerious.ID = ICSRs.IsSerious_ID LEFT JOIN SeriousnessCriteria ON SeriousnessCriteria.ID = ICSRs.SeriousnessCriterion_ID LEFT JOIN Outcomes ON Outcomes.ID = AEs.Outcome_ID LEFT JOIN DechallengeResults ON DechallengeResults.ID = AEs.DechallengeResult_ID LEFT JOIN RechallengeResults ON RechallengeResults.ID = AEs.RechallengeResult_ID JOIN MedicationsPerICSR ON MedicationsPerICSR.ID = Relations.MedicationperICSR_ID JOIN Medications ON Medications.ID = MedicationsPerICSR.Medication_ID LEFT JOIN DoseTypes ON DoseTypes.ID = Medications.DoseType_ID LEFT JOIN DrugActions ON DrugActions.ID = MedicationsPerICSR.DrugAction_ID JOIN Companies ON Companies.ID = ICSRs.Company_ID JOIN RoleAllocations ON  Companies.ID = RoleAllocations.Company_ID WHERE Companies.Active = 1 AND RoleAllocations.User_ID = 8